// import React from 'react';
// import MatchSummary from '../components/MatchSummary';
// import ScoreCard from '../components/ScoreCard';
// import ScoreComparison from '../components/ScoreComparison';
import React, { useContext, useEffect, Component } from "react";
import {Line,Doughnut} from 'react-chartjs-2';
// import { render } from 'react-dom';
import { useParams } from "react-router-dom";
import MatchFinder from '../apis/MatchFinder';
// import ScoreCard from "../components/ScoreCard";
import { Context } from '../context/Context';
import { useHistory } from "react-router-dom";
const MatchDetailPage = () => {
  const { id } = useParams();
  const head = {
    color: '#7c795d', 'fontFamily': 'Trocchi', 
    'fontSize': '60px', 'fontWeight': 'normal', 'lineHeight': '48px', 
    'textAlign': 'center'
  }
  const head2 = {
    color: '#7c795d', 'fontFamily': 'Trocchi', 
    'fontSize': '40px', 'fontWeight': 'normal', 'lineHeight': '48px', 
    'textAlign': 'center'
  }
  const head3 = {
    color: '#7c795d', 'fontFamily': 'Trocchi', 
    'fontSize': '30px', 'fontWeight': 'normal', 
    'textAlign': 'center'
  }
  const head4 = {
     'fontFamily': 'Trocchi', 
    'fontSize': '15px', 'fontWeight': 'normal', 
    'textAlign': 'center'
  }
  const divStyleLeft = {
    width: '49%', 
    float: 'left',
    'textAlign': 'center'
  };
  const divStyleRight = {
    width: '49%', 
    float: 'right',
    'textAlign': 'center'
  };
  const butn = {
    'textAlign': 'center'
  };
  // const { selectedMatch, setSelectedMatch } = useContext(
