import { useParams } from 'react-router-dom';

import MatchFinder from '../apis/MatchFinder';
import { MatchesContext } from '../context/MatchesContext';
function PointsTablePage() {
    const {year} = useParams();
    const {pointTable, setPointTable } = useContext(
        MatchesContext
      );
 useEffect(()=>{

    const fetchData = async () => {
        const response = await MatchFinder.get(`pointstable/${year}`);
        console.log(response.data.data.pointsInfo);
        setPointTable(response.data.data.pointsInfo);
    }
    fetchData();
},[]);
  return <div className='list-group'>
  <table className="table table-hover table-dark">
      <thead>
        <tr className='bg-primary'>
            <th scope = "col">Team-1</th>
            <th scope = "col">Team-2</th>
            <th scope = "col">Stadium Name</th>
            <th scope = "col">City Name</th>
            <th scope = "col">Result</th>
            <th scope = "col">F</th>
        </tr>
      </thead>
      <tbody>
          {pointTable && pointTable.map(entry=> {
              return (
                //   @T - Do we need more key attributes?
                <tr 
                onClick={() => handleMatchSelect(match.match_id)} 
                key={match.match_id} >
                <td> {match.team_name1}</td>
                <td> {match.team_name2}</td>
                <td> {match.venue_name}</td>
                <td> {match.city_name}</td>
                <td> <button className="btn btn-warning">Update</button></td>
                <td> <button className="btn btn-danger">Delete</button></td>

               </tr>
              )

          })}
      </tbody>
  </table>
</div>;;
